/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */


const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h1 {...{
      "id": "creating-blocks"
    }}>{`Creating Blocks`}</h1>
    <h2 {...{
      "id": "spec"
    }}>{`Spec`}</h2>
    <p>{`Blocks, at a minimum, feature:`}</p>
    <h3 {...{
      "id": "an-editorjs-file"
    }}>{`An `}<inlineCode parentName="h3">{`editor.js`}</inlineCode>{` file`}</h3>
    <p>{`This file should contain:`}</p>
    <ul>
      <li parentName="ul">{`The `}<inlineCode parentName="li">{`registerBlockType`}</inlineCode>{` call`}</li>
      <li parentName="ul">{`An `}<inlineCode parentName="li">{`edit`}</inlineCode>{` function`}</li>
      <li parentName="ul">{`A `}<inlineCode parentName="li">{`save`}</inlineCode>{` function.`}</li>
    </ul>
    <h3 {...{
      "id": "a-laravel-blade-view-for-the-frontend"
    }}>{`A Laravel Blade view for the frontend`}</h3>
    <p>{`Your blade view automatically has access to `}<inlineCode parentName="p">{`$attr`}</inlineCode>{` and `}<inlineCode parentName="p">{`$content`}</inlineCode>{`, which correspond with the attributes and inner content markup of the block as defined in your `}<inlineCode parentName="p">{`editor.js`}</inlineCode>{` file.`}</p>
    <p>{`The `}<inlineCode parentName="p">{`save`}</inlineCode>{` function is largely inconsequential to Copernicus, as the entire Save method is replaced behind-the-scenes with a callback to render a Blade template file in its stead. The exception is for blocks which contain other blocks (nested blocks). For these you can simply pass the `}<inlineCode parentName="p">{`innerContent`}</inlineCode>{` to save and retrieve it in your view with `}<inlineCode parentName="p">{`$content`}</inlineCode>{`.`}</p>
    <p>{`For example:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-js"
      }}>{`// ...
save: () => <InnerBlocks.Content />,
// ...
`}</code></pre>
    <p>{`Steps to construct a new block:`}</p>
    <h2 {...{
      "id": "1-add-the-block-to-the-registry"
    }}>{`1. Add the block to the registry`}</h2>
    <p>{`The registry is kept at `}<inlineCode parentName="p">{`/blocks.json`}</inlineCode>{`.`}</p>
    <p>{`Its contents are parsed by both the Acorn framework and Laravel Mix so as to reduce the burden on you.`}</p>
    <p>{`This file is a simple array. Add your block's name (excluding namespacing) to this file. You can gleefully think of this as "configuring Webpack", if you wish.`}</p>
    <p>{`Anything added to `}<inlineCode parentName="p">{`/blocks.json`}</inlineCode>{` will be automatically registered with both webpack and WordPress as long as there are
corresponding scripts and views in the `}<inlineCode parentName="p">{`/resources`}</inlineCode>{` directory.`}</p>
    <h2 {...{
      "id": "3-add-scripts"
    }}>{`3. Add scripts`}</h2>
    <p>{`JavaScript files are expected to be found at `}<inlineCode parentName="p">{`/resources/assets/scripts/{namespace}/{block}`}</inlineCode>{`.`}</p>
    <p>{`Copernicus will watch for the following files in this directory:`}</p>
    <h3 {...{
      "id": "editorjs"
    }}>{`editor.js`}</h3>
    <p><inlineCode parentName="p">{`editor.js`}</inlineCode>{` is the main entrypoint and should contain your block defintion. This is the only required asset.`}</p>
    <h3 {...{
      "id": "publicjs"
    }}>{`public.js`}</h3>
    <p><inlineCode parentName="p">{`public.js`}</inlineCode>{` will be enqueued only on pages which feature the block. It is strictly optional.`}</p>
    <h3 {...{
      "id": "reactjs"
    }}>{`react.js`}</h3>
    <p><inlineCode parentName="p">{`react.js`}</inlineCode>{`works like`}<inlineCode parentName="p">{`public.js`}</inlineCode>{` but supports JSX and automatically enqueues `}<inlineCode parentName="p">{`wp-element`}</inlineCode>{` (WordPress' React wrapper) as a dependency. It is strictly optional.`}</p>
    <h2 {...{
      "id": "4-add-styles"
    }}>{`4. Add Styles`}</h2>
    <p>{`Compass files are expected to be found at `}<inlineCode parentName="p">{`/resources/assets/styles/{namespace}/{block}`}</inlineCode>{`.`}</p>
    <p>{`Copernicus will watch for the following files in this directory:`}</p>
    <h3 {...{
      "id": "editorscss"
    }}>{`editor.scss`}</h3>
    <p><inlineCode parentName="p">{`editor.scss`}</inlineCode>{` is for editor styles. It is strictly optional.`}</p>
    <h3 {...{
      "id": "publicscss"
    }}>{`public.scss`}</h3>
    <p><inlineCode parentName="p">{`public.scss`}</inlineCode>{`, like `}<inlineCode parentName="p">{`public.js`}</inlineCode>{` and `}<inlineCode parentName="p">{`react.js`}</inlineCode>{` will be enqueued only on pages which feature the block. It is strictly optional.`}</p>
    <h2 {...{
      "id": "add-a-view"
    }}>{`Add a view.`}</h2>
    <p>{`Your Blade view is anticipated at `}<inlineCode parentName="p">{`/resources/views/blocks/{namespace}/{blockname}.blade.php`}</inlineCode>{`.`}</p>
    <p>{`Your blade view automatically has access to `}<inlineCode parentName="p">{`$attr`}</inlineCode>{` and `}<inlineCode parentName="p">{`$content`}</inlineCode>{`, which correspond with the attributes and inner content markup of the block as defined in your `}<inlineCode parentName="p">{`editor.js`}</inlineCode>{` file.`}</p>
    <p>{`This view can be extended using components, layouts, view composers, etc.`}</p>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
